import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function ApplyToTask() {
    const { taskId } = useParams(); // Récupère l'ID de la tâche depuis l'URL
    const navigate = useNavigate();
    const [task, setTask] = useState(null);
    const [message, setMessage] = useState('');
    const [status, setStatus] = useState('');
    const [error, setError] = useState('');

    // Récupération des détails de la tâche
    useEffect(() => {
        fetch(`http://localhost:8080/api/tasks/${taskId}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Failed to fetch task details');
                }
                return response.json();
            })
            .then((data) => setTask(data))
            .catch((err) => setError('Error fetching task details: ' + err.message));
    }, [taskId]);

    // Fonction pour postuler à la tâche
    const handleApply = () => {
        const userId = 'user123'; // Remplacer par l'ID de l'utilisateur connecté
        fetch(`http://localhost:8080/api/tasks/${taskId}/apply?userId=${userId}&message=${message}`, {
            method: 'POST',
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Failed to apply to task');
                }
                return response.json();
            })
            .then(() => {
                setStatus('Application submitted successfully!');
                navigate('/my-tasks'); // Redirection vers les tâches après succès
            })
            .catch((err) => setError('Failed to apply to task: ' + err.message));
    };

    // Affichage de la page
    if (!task && !error) return <p>Loading task details...</p>;
    if (error) return <p className="text-red-500">{error}</p>;

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Apply to Task</h1>
            <div className="bg-white shadow-md rounded-lg p-6">
                <h2 className="text-2xl font-semibold">{task.title}</h2>
                <p className="text-gray-600 mb-4">{task.description}</p>
                <p className="text-gray-600">Budget: ${task.budget}</p>
                <textarea
                    className="w-full p-2 border rounded mt-4"
                    placeholder="Write your application message"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                />
                <div className="flex space-x-4 mt-4">
                    <button
                        className="bg-blue-500 text-white py-2 px-4 rounded"
                        onClick={handleApply}
                    >
                        Submit Application
                    </button>
                    <button
                        className="bg-gray-300 text-gray-800 py-2 px-4 rounded"
                        onClick={() => navigate('/search-tasks')}
                    >
                        Cancel
                    </button>
                </div>
                {status && <p className="mt-4 text-green-500">{status}</p>}
            </div>
        </div>
    );
}

export default ApplyToTask;
