import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function ApplyToTask() {
    const { taskId } = useParams(); // taskId récupéré depuis l'URL
    const navigate = useNavigate();
    const [task, setTask] = useState(null); // Détails de la tâche
    const [message, setMessage] = useState(''); // Message pour l'application
    const [error, setError] = useState(''); // Message d'erreur
    const [loading, setLoading] = useState(true); // Gestion du chargement

    // Charger les détails de la tâche
    useEffect(() => {
        fetch(`http://localhost:8080/api/tasks/${taskId}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Failed to fetch task details');
                }
                return response.json();
            })
            .then((data) => {
                setTask(data);
                setLoading(false);
            })
            .catch((err) => {
                setError('Error fetching task details: ' + err.message);
                setLoading(false);
            });
    }, [taskId]);

    // Fonction pour postuler à une tâche
    const handleApply = () => {
        const userId = 'user123'; // Remplacez par l'ID de l'utilisateur connecté

        fetch(`http://localhost:8080/api/tasks/${taskId}/apply?userId=${userId}&message=${message}`, {
            method: 'POST',
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Failed to apply to task');
                }
                return response.json();
            })
            .then(() => navigate('/my-tasks')) // Redirection après succès
            .catch((err) => setError('Failed to apply to task: ' + err.message));
    };

    if (loading) return <p>Loading task details...</p>;
    if (error) return <p className="text-red-500">{error}</p>;

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold">Apply to Task</h1>
            <div className="p-4 border rounded">
                <h2 className="text-2xl font-bold">{task.title}</h2>
                <p className="text-gray-700">{task.description}</p>
                <p className="text-sm text-gray-500">
                    Budget: ${task.budget} | Category: {task.category} | Deadline: {new Date(task.deadline).toLocaleDateString()}
                </p>
            </div>
            <textarea
                className="w-full p-2 border rounded"
                placeholder="Write your application message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
            />
            <button
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                onClick={handleApply}
            >
                Apply to Task
            </button>
            {error && <p className="text-red-500">{error}</p>}
        </div>
    );
}

export default ApplyToTask;
