import React, { useState } from 'react';

function SearchTasks() {
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('');

  // Mock data for demonstration
  const tasks = [
    { id: 1, title: 'Website Design', category: 'Design', budget: '$500', deadline: '2023-07-15' },
    { id: 2, title: 'Mobile App Development', category: 'Development', budget: '$2000', deadline: '2023-08-30' },
    { id: 3, title: 'Content Writing', category: 'Writing', budget: '$200', deadline: '2023-07-10' },
  ];

  const filteredTasks = tasks.filter(task => 
    task.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
    (category === '' || task.category === category)
  );

  return (
      <>
        <div className="space-y-6">
          <h1 className="text-3xl font-bold text-gray-800">Search Tasks</h1>
          <div className="flex space-x-4">
            <input
                type="text"
                placeholder="Search tasks..."
                className="flex-1 p-2 border rounded"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />
            <select
                className="p-2 border rounded"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
            >
              <option value="">All Categories</option>
              <option value="Design">Design</option>
              <option value="Development">Development</option>
              <option value="Writing">Writing</option>
            </select>
          </div>
          <div className="bg-white shadow-md rounded-lg overflow-hidden">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Title</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Category</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Budget</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Deadline</th>
              </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
              {filteredTasks.map((task) => (
                  <tr key={task.id}>
                    <td className="px-6 py-4 whitespace-nowrap">{task.title}</td>
                    <td className="px-6 py-4 whitespace-nowrap">{task.category}</td>
                    <td className="px-6 py-4 whitespace-nowrap">{task.budget}</td>
                    <td className="px-6 py-4 whitespace-nowrap">{task.deadline}</td>
                  </tr>
              ))}
              </tbody>
            </table>
          </div>
        </div>
      </>

  );
}

export default SearchTasks;
