import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function ApplyToTask() {
    const { taskId } = useParams(); // Récupère taskId depuis l'URL
    const [task, setTask] = useState(null);
    const [message, setMessage] = useState('');
    const [status, setStatus] = useState('');

    // Fetch task details
    useEffect(() => {
        fetch(`http://localhost:8080/api/tasks/${taskId}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Failed to fetch task details');
                }
                return response.json();
            })
            .then((data) => setTask(data))
            .catch((error) => console.error('Error fetching task:', error));
    }, [taskId]);

    // Handle apply submission
    const handleApply = () => {
        fetch(`http://localhost:8080/api/tasks/${taskId}/apply?userId=user123&message=${message}`, {
            method: 'POST',
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Failed to apply to task');
                }
                return response.json();
            })
            .then(() => setStatus('Application submitted successfully'))
            .catch((error) => {
                console.error('Error applying to task:', error);
                setStatus('Failed to apply to task');
            });
    };

    if (!task) {
        return <p>Loading task details...</p>;
    }

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Apply to Task</h1>
            <div className="bg-white shadow-md rounded-lg p-6">
                <h2 className="text-2xl font-semibold">{task.title}</h2>
                <p className="text-gray-600">{task.description}</p>
                <p className="text-gray-600">Budget: {task.budget}</p>
                <textarea
                    className="w-full p-2 border rounded mt-4"
                    placeholder="Write your application message"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                />
                <button
                    className="bg-blue-500 text-white py-2 px-4 rounded mt-4"
                    onClick={handleApply}
                >
                    Submit Application
                </button>
                {status && <p className="mt-4 text-green-500">{status}</p>}
            </div>
        </div>
    );
}

export default ApplyToTask;
