import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function ApplyToTask() {
    const { taskId } = useParams(); // Récupère l'ID de la tâche depuis l'URL
    const [task, setTask] = useState(null);
    const [message, setMessage] = useState('');
    const [responseMessage, setResponseMessage] = useState('');
    const [error, setError] = useState('');

    // Fetch task details from backend
    useEffect(() => {
        fetch(`http://localhost:8080/api/tasks/${taskId}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Failed to fetch task details');
                }
                return response.json();
            })
            .then((data) => setTask(data))
            .catch((error) => setError('Failed to load task details.'));
    }, [taskId]);

    // Handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();

        // Replace 'user123' with the actual logged-in user's ID
        const userId = 'user123';

        fetch(`http://localhost:8080/api/tasks/${taskId}/apply?userId=${userId}&message=${encodeURIComponent(message)}`, {
            method: 'POST',
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Failed to apply to the task');
                }
                setResponseMessage('Your application has been submitted successfully!');
                setMessage(''); // Reset message field
            })
            .catch((error) => setError('An error occurred while applying to the task.'));
    };

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Apply to Task</h1>
            {error && <p className="text-red-600">{error}</p>}
            {task ? (
                <div className="bg-white shadow-md rounded-lg p-6">
                    <h2 className="text-2xl font-bold">{task.title}</h2>
                    <p className="text-gray-700 mt-2">{task.description}</p>
                    <p className="text-gray-500 mt-2">Category: {task.category}</p>
                    <p className="text-gray-500 mt-2">Budget: ${task.budget}</p>
                    <p className="text-gray-500 mt-2">Deadline: {task.deadline}</p>
                </div>
            ) : (
                <p>Loading task details...</p>
            )}
            <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="message">
                        Application Message
                    </label>
                    <textarea
                        id="message"
                        name="message"
                        placeholder="Write a message for your application..."
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        required
                    ></textarea>
                </div>
                <button
                    type="submit"
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                    Submit Application
                </button>
            </form>
            {responseMessage && <p className="text-green-600">{responseMessage}</p>}
        </div>
    );
}

export default ApplyToTask;
